(3, 'Low-Iron (Starphire) Glass', '{\"1\":{\"key\":\"Title\",\"value\":\"Low-Iron (Starphire) Glass\"},\"2\":{\"key\":\"Color\",\"value\":\"Transparent, blue edges\"},\"3\":{\"key\":\"Pattern\",\"value\":\"None, strips, laser-cut shapes\"},\"4\":{\"key\":\"Privacy\",\"value\":\"Low\"}}', '2024-05-04 11:18:15', '2024-06-12 17:07:01', 5, NULL, 'low_Iron_starphire_glass', NULL, NULL),
