(11, 'KOMPAS Aluminum and Glass Partition', '{\"1\":{\"key\":\"Title\",\"value\":\"KOMPAS Aluminum\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"and Glass Partition\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"The single or double glazed walls are not just a stylish glass partition wall, it is also an economical solution for your working environment.\"},\"4\":{\"key\":\"Paragraph-2\",\"value\":\"This is the only system, which allows installing glass panes horizontally in places where entrance is too small to fit large glass panels.\"},\"5\":{\"key\":\"Paragraph-3\",\"value\":\"Aluminum profile can copy the look of the custom steel partition with significantly lower price and easier installation process. This design blends particularly well in modern and industrial office design.\"}}', '2024-05-11 12:09:16', '2024-06-12 06:20:51', 8, NULL, 'kompas_aluminum_and_ glass_partition', NULL, NULL),
(12, 'Sliding doors', '{\"1\":{\"key\":\"Title\",\"value\":\"Frameless sliding\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"glass doors\"},\"3\":{\"key\":\"Description\",\"value\":\"Top-hung frameless sliding partition with polished edges for those who seek lightness and simplicity for their minimalistic interior.\"},\"4\":{\"key\":\"Paragraph-1\",\"value\":\"Multiple glass finishing options include clear, low iron, bronze, gray, and frosted.\"},\"5\":{\"key\":\"Paragraph-2\",\"value\":\"Glass panels are available in custom widths and heights, with 11 feet as the maximum height for monolithic tempered glass panels.\"},\"6\":{\"key\":\"Paragraph-3\",\"value\":\"Tempered glass thickness may be either \\u215c\\u2019\\u2019 or \\u00bd\\u2019\\u2019. Smaller thickness reduces the weight, but panels are still really durable.\"}}', '2024-05-11 12:20:53', '2024-06-12 13:44:29', 9, NULL, 'sliding_doors', NULL, NULL),
(13, 'Swing doors', '{\"1\":{\"key\":\"Title\",\"value\":\"Frameless swing\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"glass doors\"},\"3\":{\"key\":\"Description\",\"value\":\"Time-proven universal solution for home or office, with a modern minimalistic design. A Light, durable, and long-lasting option for your interior zoning.\"},\"4\":{\"key\":\"Paragraph-1\",\"value\":\"The wide range of hardware finish options.\"},\"5\":{\"key\":\"Paragraph-2\",\"value\":\"Constructed from high-quality materials \\u2013 tempered or laminated glass, customizable hardware from the best manufacturers.\"},\"6\":{\"key\":\"Paragraph-3\",\"value\":\"Eco-friendly glass walls that last ages and ages with minimal maintenance.\"}}', '2024-05-11 12:25:13', '2024-06-12 13:47:06', 9, NULL, 'swing_doors', NULL, NULL),
(14, 'Background image', NULL, '2024-05-11 12:39:59', '2024-06-11 12:21:17', 10, NULL, 'background_image', NULL, NULL),
(15, 'Swing doors', '{\"1\":{\"key\":\"Title\",\"value\":\"Loft-partitions with\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"swing doors\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Grid partitions also come with swing doors. Typically, the door is made of the identical material, but exceptions are possible.\"},\"4\":{\"key\":\"Paragraph-2\",\"value\":\"The size of the door and hardware are chosen by the client or the designer to conform to individual standards. Door accessories such as door hinges and handles are customized as well, and the client can opt for a locking or regular handle depending on the needs.\"}}', '2024-05-11 12:44:44', '2024-06-11 12:25:14', 10, NULL, 'swing_doors', NULL, NULL),
(16, 'Sliding Doors', '{\"1\":{\"key\":\"Title\",\"value\":\"Sliding\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"steel doors\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Steel partitions with sliders are made of the same steel framing and glass or opaque infills. The doors can slide to one or two sides allowing maximum usage of the opening.\"},\"4\":{\"key\":\"Paragraph-2\",\"value\":\"The sliding doors are top hung which means there are no guides on the floor and no tripping hazard. Sliding panels can be alternated with fixed sections.\"}}', '2024-05-11 12:46:28', '2024-06-11 12:27:46', 10, NULL, 'sliding_doors', NULL, NULL),
(17, 'Bi-folding doors', '{\"1\":{\"key\":\"Title\",\"value\":\"Bi-folding\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"steel doors\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Bi-folding walls work perfectly with industrial loft interiors. They can be infilled with glass and other materials just like all other steel partitions. The modules are built of the same metal framing and are connected with special hinges.\"},\"4\":{\"key\":\"Paragraph-2\",\"value\":\"The guides are installed in the ceiling. In the open position, all panels are stacked to one side leaving the opening unobstructed.\"}}', '2024-05-11 12:47:59', '2024-06-11 12:29:25', 10, NULL, 'bi_folding_steel_doors', NULL, NULL),
(18, 'Stationary steel divider', '{\"1\":{\"key\":\"Title\",\"value\":\"Stationary\"},\"2\":{\"key\":\"Subtitle\",\"value\":\"steel divider\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Stationary loft-partitions are custom welded structures made of steel profiles. Most of the time the profile is from 3\\/8\\u201d to 2\\u201d wide, but one of the pros of the system is that even profile thickness can be customized.\"},\"4\":{\"key\":\"Paragraph-2\",\"value\":\"Such a partition is anchored to the walls, floor, and ceiling with special screws. In most cases, the partitions are infilled with the glass, but steel and other materials can be used as well. Framing can also be decorated.\"}}', '2024-05-11 12:49:43', '2024-06-11 12:31:20', 10, NULL, 'Stationary steel divider', NULL, NULL),
(19, 'Sliding', '{\"1\":{\"key\":\"Title\",\"value\":\"CGF-Series Sliding\"},\"2\":{\"key\":\"Description\",\"value\":\"Top-hung sliding door track system with a soft-close mechanism. Create your own configuration using three main design options.\"},\"3\":{\"key\":\"Title-1\",\"value\":\"Perimeter\"},\"4\":{\"key\":\"Subtitle-1\",\"value\":\"frame\"},\"5\":{\"key\":\"Title-2\",\"value\":\"Horizontal\"},\"6\":{\"key\":\"Subtitle-2\",\"value\":\"mullions\"},\"7\":{\"key\":\"Title-3\",\"value\":\"Criss-cross\"},\"8\":{\"key\":\"Subtitle-3\",\"value\":\"design\"}}', '2024-05-11 13:07:14', '2024-06-12 16:49:17', 3, NULL, 'sliding', NULL, NULL),
(20, 'Swing', '{\"1\":{\"key\":\"Title\",\"value\":\"CGF-Series Swing\"},\"2\":{\"key\":\"Description\",\"value\":\"Thin decorative frames and mullions \\u2013 ideal for giving your interior a classy texture without losing transparency and airiness. Create your own configuration using three main design options.\"},\"3\":{\"key\":\"Title-1\",\"value\":\"Perimeter\"},\"4\":{\"key\":\"Subtitle-1\",\"value\":\"frame\"},\"5\":{\"key\":\"Title-2\",\"value\":\"Horizontal\"},\"6\":{\"key\":\"Subtitle-2\",\"value\":\"mullions\"},\"7\":{\"key\":\"Title-3\",\"value\":\"Criss-cross\"},\"8\":{\"key\":\"Subtitle-3\",\"value\":\"design\"}}', '2024-05-11 13:12:57', '2024-06-12 16:54:14', 3, NULL, 'swing', NULL, NULL),
(21, 'Background image', '{\"1\":{\"key\":\"Icon-1\",\"value\":\"MONOLITHIC GLASS WITH DECORATIVE MULLIONS\"},\"2\":{\"key\":\"Icon-2\",\"value\":\"EASY AND TROUBLE-FREE INSTALLATION\"},\"3\":{\"key\":\"Icon-3\",\"value\":\"WIDE RANGE OF CONFIGURATIONS\"}}', '2024-05-11 13:15:16', '2024-06-12 16:46:08', 3, NULL, 'background_image', NULL, NULL),
