(237, 'AutoCAD draftsman', '{\"1\":{\"key\":\"Title\",\"value\":\"AutoCAD draftsman\"},\"2\":{\"key\":\"Description\",\"value\":\"Provide necessary drawings in AutoCAD for the manufacturer of glass and steel constructions according to the measurements and calculations provided.\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Crystalia Glass is interested in hiring qualified drafting engineers with full command of AutoCAD\"},\"4\":{\"key\":\"Title-2\",\"value\":\"Responsibilities:\"},\"5\":{\"key\":\"Paragraph-2\",\"value\":\"Create necessary drawings in AutoCAD for the glass and steel manufacturer according to the measurements and calculations provided\"},\"6\":{\"key\":\"Paragraph-3\",\"value\":\"Assist Project Managers and estimating department in analyzing the cost impact for change orders\"},\"7\":{\"key\":\"Paragraph-4\",\"value\":\"Compilation of a hardware list for the project\"},\"8\":{\"key\":\"Paragraph-5\",\"value\":\"Analyze and revise drawings based on engineer\\u2019s comments, architects\\u2019 changes, site conditions, etc.\"},\"9\":{\"key\":\"Title-3\",\"value\":\"Required Skills and Qualifications:\"},\"10\":{\"key\":\"Paragraph-6\",\"value\":\"General knowledge of glass and steel is a PLUS;\"},\"11\":{\"key\":\"Paragraph-7\",\"value\":\"Advanced knowledge of AutoCAD;\"},\"12\":{\"key\":\"Paragraph-8\",\"value\":\"Blueprint reading\"},\"13\":{\"key\":\"Paragraph-9\",\"value\":\"Compliance with drawing rules and standards\"},\"14\":{\"key\":\"Paragraph-10\",\"value\":\"Computer savvy (Adobe Acrobat Pro, Excel, Click up, etc.)\"},\"15\":{\"key\":\"Paragraph-11\",\"value\":\"An engineering degree is encouraged, but not required.\"},\"16\":{\"key\":\"Paragraph-12\",\"value\":\"Legal documentation\"},\"17\":{\"key\":\"Paragraph-13\",\"value\":\"Ability to manage time appropriately and meet deadlines\"},\"18\":{\"key\":\"Title-4\",\"value\":\"Benefits:\"},\"19\":{\"key\":\"Paragraph-14\",\"value\":\"Paid sick days and vacation\"}}', '2024-06-14 11:46:29', '2024-06-14 11:46:29', 72, NULL, 'autocad_draftsman', NULL, NULL),
(238, 'Installer', '{\"1\":{\"key\":\"Title-1\",\"value\":\"Installer\"},\"2\":{\"key\":\"Description\",\"value\":\"Perform a full range of processes related to Crystalia Glass systems installation.\"},\"3\":{\"key\":\"Paragraph-1\",\"value\":\"Looking for skilled, motivated, and qualified candidates to join our Installer team.\"},\"4\":{\"key\":\"Title-2\",\"value\":\"Responsibilities:\"},\"5\":{\"key\":\"Paragraph-2\",\"value\":\"Perform a full range of processes related to Crystalia Glass systems installation following high skill standards.\"},\"6\":{\"key\":\"Title-3\",\"value\":\"Required Skills and Qualifications:\"},\"7\":{\"key\":\"Paragraph-3\",\"value\":\"Have experience in the installation of:\\r\\nShower doors\\/Storefronts\\/Frameless and Framed Partitions\\/Glass railings\"},\"8\":{\"key\":\"Paragraph-4\",\"value\":\"Knowledge about glazing and silicone\"},\"9\":{\"key\":\"Paragraph-5\",\"value\":\"Basic knowledge in metalwork and metal finishing\"},\"10\":{\"key\":\"Paragraph-6\",\"value\":\"Have knowledge in general construction\"},\"11\":{\"key\":\"Paragraph-7\",\"value\":\"Be able to work in a team environment\"},\"12\":{\"key\":\"Paragraph-8\",\"value\":\"Speak English (Russian would be a bonus!)\"},\"13\":{\"key\":\"Title-4\",\"value\":\"Required License\\/Certification:\"},\"14\":{\"key\":\"Paragraph-9\",\"value\":\"Driving License\"},\"15\":{\"key\":\"Paragraph-10\",\"value\":\"OSHA\"},\"16\":{\"key\":\"Paragraph-11\",\"value\":\"Candidate must have their work authorization\"},\"17\":{\"key\":\"Title-5\",\"value\":\"Benefits:\"},\"18\":{\"key\":\"Paragraph-12\",\"value\":\"Paid sick days and vacation\"}}', '2024-06-14 11:51:59', '2024-06-14 11:51:59', 72, NULL, 'installer', NULL, NULL);
